sys_variable_value.value:
(function execute(inputs, outputs) {
  var responseBody = JSON.parse(inputs.response);
  if(inputs.status!=200){
   	var errorMsg =  responseBody.error.message;
    var errorDetail = responseBody.error.detail;
    throw "Error retrieving incident. Message: "+errorMsg + " Details:"+errorDetail;
  } else {
    var result = responseBody.result[0];
    outputs.correlation_id = result.correlation_id;
    outputs.short_description = result.short_description;
    outputs.description=result.description;
    outputs.state=result.state;
    outputs.caller_id=result.caller_id;
    outputs.business_service=result.business_service;
    outputs.category=result.category;
    outputs.subcategory=result.subcategory;
    outputs.impact=result.impact;
    outputs.urgency=result.urgency;
    outputs.cmdb_ci=result.cmdb_ci;
    outputs.assigned_to=result.assigned_to;
    outputs.assignment_group=result.assignment_group;
    outputs.contact_type = result.contact_type;

  }
})(inputs, outputs);

